--
-- 表的结构 `th_category`
--

CREATE TABLE `th_category` (
  `id` int(11) NOT NULL,
  `pid` mediumint(9) NOT NULL DEFAULT '0' COMMENT '父导航id',
  `catename` varchar(500) COLLATE utf8_bin NOT NULL COMMENT '导航名',
  `type` mediumint(9) NOT NULL COMMENT '0表示单页 1表示列表页',
  `orders` mediumint(9) NOT NULL COMMENT '排序',
  `url` varchar(500) COLLATE utf8_bin NOT NULL COMMENT '导航链接地址',
